import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./framework.css";
import "./master.css";
import photo from "./profile-user.png";
import change from "./cigarette.png";
import setting from "./settings.png";
import multipe from "./multiple-users-silhouette.png";
import friend from "./friends.png";
import { db } from "../fireba/firebase";
import { doc, getDoc, getDocs, collection, updateDoc } from "firebase/firestore";
import { useAuth } from "../context/AuthContext";
import { database, ref, push, set } from "../fireba/firebase";

export default function Friend() {
  const { currentUser } = useAuth();
  const [users, setUsers] = useState([]);
  const [friends, setFriends] = useState([]);
  const [isFriendFormVisible, setFriendFormVisible] = useState(false);
  const [message, setMessage] = useState("");
  const [loader, setLoader] = useState(false);
  const [alert, setAlert] = useState(false);

  useEffect(() => {
    fetchUsers();
    fetchFriends();
  }, []);

  const fetchUsers = async () => {
    try {
      const usersSnapshot = await getDocs(collection(db, "users"));
      const userList = usersSnapshot.docs
        .map((doc) => ({ id: doc.id, ...doc.data() }))
        .filter((user) => user.id !== currentUser.uid); // Exclude current user
      setUsers(userList);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const fetchFriends = async () => {
    try {
      const userRef = doc(db, "users", currentUser.uid);
      const userSnap = await getDoc(userRef);
      if (userSnap.exists()) {
        const friendIds = userSnap.data().friends || [];
        const friendsData = await Promise.all(
          friendIds.map(async (friendId) => {
            const friendDoc = await getDoc(doc(db, "users", friendId));
            return { id: friendId, ...friendDoc.data() };
          })
        );
        setFriends(friendsData);
      }
    } catch (error) {
      console.error("Error fetching friends:", error);
    }
  };

  const addFriend = async (friendId) => {
    try {
      const userRef = doc(db, "users", currentUser.uid);
      const updatedFriends = [...new Set([...friends.map((f) => f.id), friendId])];
      await updateDoc(userRef, { friends: updatedFriends });

      const friendDoc = await getDoc(doc(db, "users", friendId));
      setFriends([...friends, { id: friendId, ...friendDoc.data() }]);
    } catch (error) {
      console.error("Error adding friend:", error);
    }
  };

  const removeFriend = async (friendId) => {
    try {
      const userRef = doc(db, "users", currentUser.uid);
      const updatedFriends = friends.filter((friend) => friend.id !== friendId).map((f) => f.id);
      await updateDoc(userRef, { friends: updatedFriends });

      setFriends((prev) => prev.filter((friend) => friend.id !== friendId));
    } catch (error) {
      console.error("Error removing friend:", error);
    }
  };

  const handleMessageSubmit = (e) => {
    e.preventDefault();
    setLoader(true);
    const messageRef = ref(database, "chat");
    const newMessageRef = push(messageRef);

    set(newMessageRef, { message })
      .then(() => {
        setLoader(false);
        setAlert(true);
        setMessage("");
        setTimeout(() => setAlert(false), 3000);
      })
      .catch((error) => {
        console.error("Error sending message:", error);
        setLoader(false);
      });
  };

  const UserList = ({ list, action, actionText }) => (
    <ul className="user-list">
      {list.map((user) => (
        <li key={user.id} className="user-item d-flex align-center">
          <span style={{ margin: "10px" }}>{user.name}</span>
          <button
            className="btn bg-blue c-white ml-10"
            onClick={() => action(user.id)}
            style={{ margin: "5px" }}
          >
            {actionText}
          </button>
        </li>
      ))}
    </ul>
  );

  return (
    <div className="page d-flex">
      {/* Sidebar */}
      <div className="sidebar bg-white p-20">
        <h3 className="txt-c mt-0">habit-up</h3>
        <ul>
          {/* Links */}
          {[{ link: "/dashboard", img: photo, text: "Dashboard" },
          { link: "/setting", img: setting, text: "Setting" },
          { link: "/yourhabits", img: change, text: "Habit" },
          { link: "/challenges", img: multipe, text: "Challenge" },
          { link: "/friends", img: friend, text: "Friends" }].map(({ link, img, text }) => (
            <li key={text}>
              <Link to={link} className="d-flex align-center fs-14 c-black rad-6 p-10">
                <img src={img} alt={text} style={{ width: "24px" }} />
                <span>{text}</span>
              </Link>
            </li>
          ))}
        </ul>
      </div>

      {/* Content */}
      <div className="content w-full">
        <h1 className="p-relative">Friends</h1>
        <button
          className="btn bg-blue c-white"
          onClick={() => setFriendFormVisible(!isFriendFormVisible)}
          style={{ position: "absolute", top: "20px", right: "20px" }}
        >
          {isFriendFormVisible ? "Close" : "+"}
        </button>

        {isFriendFormVisible && (
          <UserList list={users} action={addFriend} actionText="Add Friend" />
        )}

        <div className="wrapper d-grid gap-20">
          {/* Friends */}
          <div className="tasks p-20 bg-white rad-10">
            <h2 className="mt-0 mb-20">List of Friends</h2>
            <UserList list={friends} action={removeFriend} actionText="Remove Friend" />
          </div>

          {/* Chat */}
          <div className="tasks p-20 bg-white rad-10">
            <h2 className="mt-0 mb-20">Chat</h2>
            <form onSubmit={handleMessageSubmit}>
              <label>Message:</label>
              <textarea
                placeholder="Your Message"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                required
              ></textarea>
              <button type="submit" className="btn-primary" disabled={loader}>
                {loader ? "Submitting..." : "Send Message"}
              </button>
            </form>
            {alert && <div className="alert">Your message has been sent!</div>}
          </div>
        </div>
      </div>
    </div>
  );
}
