import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./framework.css";
import "./master.css";
import photo from "./profile-user.png";
import change from "./cigarette.png";
import setting from "./settings.png";
import multipe from "./multiple-users-silhouette.png";
import friend from "./friends.png";
import { db } from "../fireba/firebase"; // Firebase configuration
import { doc, getDoc, getDocs, collection, updateDoc } from "firebase/firestore";
import { useAuth } from "../context/AuthContext";

function UserList({ title, users, actionLabel, onAction, disableAction }) {
  return (
    <div className="user-list-section bg-white p-20 rad-6">
      <h2>{title}</h2>
      <ul>
        {users.map((user) => (
          <li key={user.id} className="user-item d-flex align-center">
            <span style={{ margin: "10px" }}>{user.name}</span>
            <button
              className={`btn ${disableAction ? "bg-gray" : "bg-blue"} c-white ml-10`}
              onClick={() => !disableAction && onAction(user.id)}
              disabled={disableAction}
              style={{ margin: "5px" }}
            >
              {actionLabel}
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default function Friend() {
  const { currentUser } = useAuth(); // Get current user info
  const [users, setUsers] = useState([]); // Store list of users
  const [friends, setFriends] = useState([]); // Current user's friends
  const [isFormVisible, setIsFormVisible] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  // Fetch all users except the current user
  const fetchUsers = async () => {
    try {
      const usersSnapshot = await getDocs(collection(db, "users"));
      const userList = usersSnapshot.docs
        .map((doc) => ({ id: doc.id, ...doc.data() }))
        .filter((user) => user.id !== currentUser.uid); // Exclude current user
      setUsers(userList);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  // Fetch current user's friends
  const fetchFriends = async () => {
    try {
      const userRef = doc(db, "users", currentUser.uid);
      const userSnap = await getDoc(userRef);
      if (userSnap.exists()) {
        const friendIds = userSnap.data().friends || [];
        const friendsData = await Promise.all(
          friendIds.map(async (friendId) => {
            const friendDoc = await getDoc(doc(db, "users", friendId));
            return { id: friendId, ...friendDoc.data() };
          })
        );
        setFriends(friendsData);
      }
    } catch (error) {
      console.error("Error fetching friends:", error);
    }
  };

  const updateFriends = async (updatedFriends) => {
    try {
      const userRef = doc(db, "users", currentUser.uid);
      await updateDoc(userRef, { friends: updatedFriends });
    } catch (error) {
      console.error("Error updating friends list:", error);
    }
  };

  // Add friend
  const addFriend = async (friendId) => {
    setIsLoading(true);
    try {
      const updatedFriends = [...new Set([...friends.map((f) => f.id), friendId])]; // Avoid duplicates
      await updateFriends(updatedFriends);

      const friendDoc = await getDoc(doc(db, "users", friendId));
      setFriends([...friends, { id: friendId, ...friendDoc.data() }]);
    } catch (error) {
      console.error("Error adding friend:", error);
    } finally {
      setIsLoading(false);
    }
  };

  // Remove friend
  const removeFriend = async (friendId) => {
    setIsLoading(true);
    try {
      const updatedFriends = friends.filter((f) => f.id !== friendId).map((f) => f.id);
      await updateFriends(updatedFriends);
      setFriends(friends.filter((f) => f.id !== friendId));
    } catch (error) {
      console.error("Error removing friend:", error);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchUsers();
    fetchFriends();
  }, []);

  return (
    <div className="page d-flex">
      {/* Sidebar */}
      <div className="sidebar bg-white p-20">
        <h3 className="p-relative txt-c mt-0">habit-up</h3>
        <ul>
          {/* Sidebar links */}
          {[["Dashboard", photo], ["Setting", setting], ["Habit", change], ["Challenge", multipe], ["Friends", friend]].map(([label, icon]) => (
            <li key={label}>
              <Link to={`/${label.toLowerCase()}`} className="d-flex align-center fs-14 c-black rad-6 p-10">
                <img src={icon} alt={label} style={{ width: "24px" }} />
                <span>{label}</span>
              </Link>
            </li>
          ))}
        </ul>
      </div>

      {/* Main Content */}
      <div className="content w-full">
        <h1 className="p-relative">Friends</h1>
        {isFormVisible && (
          <UserList
            title="Add Friends"
            users={users}
            actionLabel="Add Friend"
            onAction={addFriend}
            disableAction={isLoading}
          />
        )}
        <button className="btn bg-blue c-white" onClick={() => setIsFormVisible(!isFormVisible)}>
          {isFormVisible ? "Close" : "Add Friend"}
        </button>
        <UserList
          title="List of Friends"
          users={friends}
          actionLabel="Remove Friend"
          onAction={removeFriend}
          disableAction={isLoading}
        />
      </div>
    </div>
  );
}
