import React, { useState, useEffect } from "react";
import "./framework.css";
import "./master.css";
import photo from "./profile-user.png";
import change from "./cigarette.png";
import setting from "./settings.png";
import multipe from "./multiple-users-silhouette.png";
import friend from "./friends.png";
import { Link } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { doc, getDoc, setDoc, updateDoc, arrayUnion } from "firebase/firestore";
import { db } from "../fireba/firebase";
export default function Habit() { 
  const [isFormVisible, setIsFormVisible] = useState(false);
  const [selectedFormId, setSelectedFormId] = useState(null);
    const openForm = (formId) => {
        setIsFormVisible(true);
        setSelectedFormId(formId);
      };
    
      const closeForm = () => {
        setIsFormVisible(false);
        setSelectedFormId(null);
      };
        const [myhabit, setmyhabit] = useState([]);
        const [newmyhabit, setnewmyhabit] = useState({habit: "", goal: "" ,reminder:""});
        const [error, setError] = useState("");
        const { currentUser} = useAuth();
         useEffect(() => {
            if (!currentUser) return;
        
            async function loadmyhabitData() {
              try {
                const userRef = doc(db, "myhabit", currentUser.uid);
                const  myhabitSnap = await (
                  getDoc(userRef));
        
                if (myhabitSnap.exists()) setUserData(myhabitSnap.data());
              } catch (err) {
                console.error("Error loading data:", err);
                setError("Failed to load data.");
              }
            }
        
            loadUserData();
          }, [currentUser]);
        const handleAddhabit = async () => {
          if (!newmyhabit.habit || !newmyhabit.goal || !newmyhabit.reminder) {
            alert("Please fill in all fields.");
            return;
          }
          const updatedmyhabits = [
            ...myhabit,
            { id: tasks.length + 1, habit: newmyhabit.title,goal:newmyhabit.remarque, done: false },
          ];
      
          try {
            const myhabitRef = doc(db, "myhabit", currentUser.uid);
            await setDoc(myhabitRef, { habit: updatedmyhabits});
            setTasks(updatedmyhabits);
            setNewTask({ habit: "", goal: "",rem });
          } catch (error) {
            console.error("Error saving task:", error);
          }
        };
      
    return (
<div class="page d-flex">
            <div class="sidebar bg-white p-20 p-relative">
                <h3 class="p-relative txt-c mt-0">habit-up</h3>
                <ul>
                    <li>
                        <Link to="/dashboard" className=" d-flex align-center fs-14 c-black rad-6 p-10"><img src={photo} style={{ width: '24px' }} /><span>Dashboard</span></Link>
                    </li>
                    <li>
                        <Link to="/setting" className=" d-flex align-center fs-14 c-black rad-6 p-10"><img src={setting} style={{ width: '24px' }} /><span>Setting</span></Link>
                    </li>
                    <li>
                        <Link to="/yourhabits" className=" d-flex align-center fs-14 c-black rad-6 p-10"><img src={change} style={{ width: '24px' }} /><span>Habit</span></Link>
                    </li>
                    <li>
                        <Link to="/challenges" className=" d-flex align-center fs-14 c-black rad-6 p-10"><img src={multipe} style={{ width: '24px' }} /><span>Challenge</span></Link>
                    </li>
                    <li>
                        <Link to="/friends" className=" d-flex align-center fs-14 c-black rad-6 p-10"><img src={friend} style={{ width: '24px' }} /><span>Friends</span></Link>
                    </li>
                </ul>
                </div>
         <div className="content w-full">
          <h1 className="p-relative">My habits</h1>
       
          {isFormVisible && (
        <div className="add-challenge-form bg-white p-20 rad-6 m-20">
          <h3>Add a New Habit</h3>
          <input
            type="text"
            placeholder="Habit"
           // value={newChallenge.title}
            //onChange={(e) => setNewChallenge({ ...newChallenge, title: e.target.value })}
            className="d-block mb-10 w-full"
          />
        <div className="labels" style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
  <label htmlFor="goal" style={{  color: "black" }}>Goal:</label>
  <input 
    className="input-form" 
    type="number" 
    id="goal" 
    min="1" 
    defaultValue="1" 
    required 
  />
  <select id="goalType" style={{  color: "black" }}>
    <option value="times" className="d-block mb-10 w-full">Times</option>
    <option value="minutes" className="d-block mb-10 w-full">Minutes</option>
    <option value="hours" className="d-block mb-10 w-full">Hours</option>
  </select>
  <select id="goalFrequency" style={{ color: "black" }}>
    <option value="day">Per Day</option>
    <option value="week"style={{ margin: "5px" }}>Per Week</option>
  </select>
</div>

          <input
            type="text"
            placeholder="reminder"
            //value={newChallenge.description}
            //onChange={(e) =>setNewChallenge({ ...newChallenge, description: e.target.value })
            className="d-block mb-10 w-full"
            style={{ margin: "5px" }}
          />
    
    
          <button  className="btn bg-blue c-white">
           
            Add Challenge
          </button>
          <button className="btn bg-blue c-white" onClick={closeForm} style={{margin:'5px'}}>
              Close
            </button>
        </div>)}
          <div className="wrapper d-grid gap-20"> 
        <div className="tasks p-20 bg-white rad-10">
        <h2 className="m-0">current habit</h2>
        <div id="habitsContainer">
        <div id="completedHabits" class="habit-box">
        </div>
            </div>
          </div> 
          <div className="tasks p-20 bg-white rad-10">
        <h2 className="m-0">completed habit</h2>
          </div>  
          </div>  

          <button className="btn bg-blue c-white" onClick={openForm} style={{ position: 'absolute',top: '20px',
  right: '20px'}}>
          +
        </button>
          </div>  
          </div>  
    );
    };
