import React, { useState, useEffect } from "react";
import "./framework.css";
import "./master.css";
import photo from "./profile-user.png";
import change from "./cigarette.png";
import setting from "./settings.png";
import multipe from "./multiple-users-silhouette.png";
import friend from "./friends.png";
import team1 from "./team-01.png";
import team2 from "./team-02.png";
import team3 from "./team-03.png";
import team4 from "./team-04.png";
import trash from "./trash-can.png";
import { Link } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { doc, getDoc, setDoc, updateDoc } from "firebase/firestore";
import { db } from "../fireba/firebase";

export default function Challenge() {
  const [isFormVisible, setIsFormVisible] = useState(false);
  const [isChallengeVisible, setIsChallengeVisible] = useState(false);
  const [selectedChallengeId, setSelectedChallengeId] = useState(null);
  const [selectedFormId, setSelectedFormId] = useState(null);
  const [challenges, setChallenges] = useState([]);
  const { currentUser } = useAuth();

  const handleDeleteChallenge = async (challengeId) => {
    const updatedChallenges = challenges.filter(
      (challenge) => challenge.id !== challengeId
    );

    try {
      const challengesRef = doc(db, "challenge", currentUser.uid);
      await updateDoc(challengesRef, { challenges: updatedChallenges });
      setChallenges(updatedChallenges);
    } catch (error) {
      console.error("Error deleting task:", error);
    }
  };

  const openForm = (formId) => {
    setIsFormVisible(true);
    setSelectedFormId(formId);
  };

  const closeForm = () => {
    setIsFormVisible(false);
    setSelectedFormId(null);
  };

  const openChallenge = (challengeId) => {
    setIsChallengeVisible(true);
    setSelectedChallengeId(challengeId);
  };

  const closeChallenge = () => {
    setIsChallengeVisible(false);
    setSelectedChallengeId(null);
  };

  const [newChallenge, setNewChallenge] = useState({
    title: "",
    date: "",
    description: "",
    progress: 0,
    action: "",
  });

  const mainChallenges = [
    {
      id: 1,
      title: "Elzero Dashboard",
      date: "15/10/2021",
      description: "Elzero Dashboard Project Design And Programming And Hosting",
      action: "Learn new Language",
      team: [team1, team2, team3, team4],
    },
    {
      id: 2,
      title: "Academy Portal",
      date: "15/6/2022",
      description: "Academy Portal Project Design And Programming",
      action: "Go to Gym",
      team: [team3, team1, team4],
    },
    {
      id: 3,
      title: "Chatting Application",
      date: "15/6/2022",
      description: "Chatting Application Project Design",
      action: "Learn Design",
      team: [team3, team2, team4],
    },
    {
      id: 4,
      title: "Ahmed Dashboard",
      date: "15/6/2022",
      description: "Ahmed Dashboard Project Design And Programming And Hosting",
      action: "Stop smoking",
      team: [team1, team2, team3, team4],
    },
    {
      id: 5,
      title: "Ahmed Portal",
      date: "15/6/2022",
      description: "Ahmed Portal Project Design And Programming",
      action: "Play football",
      team: [team3, team4, team1],
    },
  ];

  const loadUserData = async () => {
    try {
      const challengeRef = doc(db, "challenge", currentUser.uid);
      const challengeSnap = await getDoc(challengeRef);

      if (challengeSnap.exists()) {
        setChallenges(challengeSnap.data().challenges || []);
      }
    } catch (err) {
      console.error("Failed to load data:", err);
    }
  };

  useEffect(() => {
    if (currentUser) {
      loadUserData();
    }
  }, [currentUser]);

  const handleAddChallenge = async () => {
    if (
      !newChallenge.title ||
      !newChallenge.description ||
      !newChallenge.action
    ) {
      alert("Please fill all fields.");
      return;
    }

    const updatedChallenges = [
      ...challenges,
      {
        id: challenges.length + 1,
        ...newChallenge,
        team: [team4],
      },
    ];

    try {
      const challengeRef = doc(db, "challenge", currentUser.uid);
      await setDoc(challengeRef, { challenges: updatedChallenges });
      setChallenges(updatedChallenges);
      setNewChallenge({
        title: "",
        date: "",
        description: "",
        progress: 0,
        action: "",
      });
    } catch (error) {
      console.error("Error saving challenge:", error);
    }
  };

  const Enrol = async (challenge) => {
    try {
      const habitRef = doc(db, "habit", currentUser.uid);
      const newHabit = {
        title: challenge.title,
        date: challenge.date,
        description: challenge.description,
        action: challenge.action,
        team: challenge.team,
      };
      await setDoc(habitRef, newHabit);
      closeChallenge();
    } catch (error) {
      console.error("Error enrolling to challenge:", error);
    }
  };

  return (
    <div className="page d-flex">
      {/* Sidebar */}
      <div className="sidebar bg-white p-20 p-relative">
        <h3 className="p-relative txt-c mt-0">habit-up</h3>
        <ul>
          <li>
            <Link
              to="/dashboard"
              className="d-flex align-center fs-14 c-black rad-6 p-10"
            >
              <img src={photo} style={{ width: "24px" }} alt="Dashboard Icon" />
              <span>Dashboard</span>
            </Link>
          </li>
          <li>
            <Link
              to="/setting"
              className="d-flex align-center fs-14 c-black rad-6 p-10"
            >
              <img src={setting} style={{ width: "24px" }} alt="Settings Icon" />
              <span>Setting</span>
            </Link>
          </li>
          <li>
            <Link
              to="/yourhabits"
              className="d-flex align-center fs-14 c-black rad-6 p-10"
            >
              <img src={change} style={{ width: "24px" }} alt="Habit Icon" />
              <span>Habit</span>
            </Link>
          </li>
          <li>
            <Link
              to="/challenges"
              className="d-flex align-center fs-14 c-black rad-6 p-10"
            >
              <img
                src={multipe}
                style={{ width: "24px" }}
                alt="Challenge Icon"
              />
              <span>Challenge</span>
            </Link>
          </li>
          <li>
            <Link
              to="/friends"
              className="d-flex align-center fs-14 c-black rad-6 p-10"
            >
              <img src={friend} style={{ width: "24px" }} alt="Friends Icon" />
              <span>Friends</span>
            </Link>
          </li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="content w-full">
        <h1 className="p-relative">Community Challenges</h1>
        {isFormVisible && (
          <div className="add-challenge-form bg-white p-20 rad-6 m-20">
            <h3>Add a New Challenge</h3>
            <input
              type="text"
              placeholder="Title"
              value={newChallenge.title}
              onChange={(e) =>
                setNewChallenge({ ...newChallenge, title: e.target.value })
              }
              className="d-block mb-10 w-full"
            />
            <input
              type="text"
              placeholder={new Date().toLocaleString()}
              value={new Date().toLocaleString()}
              onChange={(e) =>
                setNewChallenge({ ...newChallenge, date: e.target.value })
              }
              className="d-block mb-10 w-full"
            />
            <input
              type="text"
              placeholder="Description"
              value={newChallenge.description}
              onChange={(e) =>
                setNewChallenge({
                  ...newChallenge,
                  description: e.target.value,
                })
              }
              className="d-block mb-10 w-full"
            />
            <input
              type="text"
              placeholder="Action"
              value={newChallenge.action}
              onChange={(e) =>
                setNewChallenge({ ...newChallenge, action: e.target.value })
              }
              className="d-block mb-10 w-full"
            />
            <button
              className="btn btn-primary"
              onClick={handleAddChallenge}
            >
              Add Challenge
            </button>
            <button className="btn btn-secondary" onClick={closeForm}>
              Cancel
            </button>
          </div>
        )}
        {isChallengeVisible && selectedChallengeId && (
          <div className="challenge-popup">
            <div className="popup-content bg-white p-20 rad-6 m-20">
              {mainChallenges
                .filter((challenge) => challenge.id === selectedChallengeId)
                .map((challenge) => (
                  <div key={challenge.id}>
                    <h3>{challenge.title}</h3>
                    <p>Date: {challenge.date}</p>
                    <p>Description: {challenge.description}</p>
                    <p>Action: {challenge.action}</p>
                    <div className="team">
                      {challenge.team.map((member, index) => (
                        <img
                          key={index}
                          src={member}
                          alt={`Team member ${index + 1}`}
                        />
                      ))}
                    </div>
                    <button
                      className="btn btn-primary"
                      onClick={() => Enrol(challenge)}
                    >
                      Enroll
                    </button>
                    <button className="btn btn-secondary" onClick={closeChallenge}>
                      Close
                    </button>
                  </div>
                ))}
            </div>
          </div>
        )}
        <div className="add-challenge">
          <button className="btn btn-primary" onClick={() => openForm(null)}>
            Add New Challenge
          </button>
        </div>
        <div className="challenges-list">
          {mainChallenges.map((challenge) => (
            <div
              key={challenge.id}
              className="challenge-card bg-white rad-6 p-20 m-20"
            >
              <h3>{challenge.title}</h3>
              <p>{challenge.date}</p>
              <p>{challenge.description}</p>
              <div className="team">
                {challenge.team.map((member, index) => (
                  <img
                    key={index}
                    src={member}
                    alt={`Team member ${index + 1}`}
                  />
                ))}
              </div>
              <button
                className="btn btn-primary"
                onClick={() => openChallenge(challenge.id)}
              >
                View Challenge
              </button>
              <button
                className="btn btn-danger"
                onClick={() => handleDeleteChallenge(challenge.id)}
              >
                Delete
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
